using Lab06.BLL.Helpers;
using NUnit.Framework;
using System;
using System.IO;

namespace Lab06.BLL.Tests.Helpers
{
    [TestFixture]
    public class ImageConverterHelperTests
    {
        [Test]
        public void FromByteArrayToString_ShouldReturnString()
        {
            // Arrange
            var workingDirectory = Environment.CurrentDirectory;
            var imagePath = Directory.GetParent(workingDirectory).Parent.Parent.Parent.FullName + "/Lab06.DAL/Resources/carousel.png";
            string expectedString = "data:image/png;base64,";

            // Act
            var actualString = ImageConverterHelper.FromByteArrayToString(File.ReadAllBytes(imagePath));

            // Assert
            Assert.AreEqual(expectedString, actualString);
        }
    }
}
